import React from 'react';
import { useState, useEffect } from 'react';
import withRouter from 'react-router-dom/withRouter';
import DateTime from 'luxon/src/datetime';
import axios from 'axios';
import Header from './Header';

function BlogContent() {
    const [articles, setArticles] = useState()

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/api/articles')
        .then(res => {
            setArticles(res.data)
        })
    }, [false])

    return (
        <React.Fragment>
            <Header header={'Blog'} subtitle={'Articles about various topics related to the tech industry'}/>
            {articles ? articles.map(article => <p>{article.title}, {DateTime.fromMillis(article.publish_date.$date).toISODate}</p>): <p>loading...</p>}
        </React.Fragment>
    );
}


export default withRouter(BlogContent);
