import React, { useState } from 'react';
import { withRouter } from 'react-router-dom';
import { Link as RouterLink } from 'react-router-dom';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Switch from '@material-ui/core/Switch';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import useMediaQuery from '@material-ui/core/useMediaQuery';


const useStyles = makeStyles((theme) => ({
    root : {
        flexGrow: 1,
    },
    icons: {
        flexGrow: 1,
    },
}));

function Navbar(props){
    const theme = useTheme()
    const screenSize = useState(useMediaQuery(theme.breakpoints.down('sm')))
    let changeSize = screenSize ? 'small': 'large';
    const classes = useStyles();
        return (
            <div class={classes.root}>
            <AppBar position="sticky">
                    <Toolbar>
                        <Box className={classes.icons}>
                            <Button variant="contained" color="primary" component={RouterLink} to="/" size={changeSize}>
                                James Hutson
                            </Button>
                        </Box>
                        <Box p={1}>
                            {notSmallScreen &&
                            <Button variant="contained" color="primary" component={RouterLink} to="/portfolio">
                                Portfolio
                            </Button>
                            }
                            {smallScreen &&
                            <Button variant="contained" color="primary" component={RouterLink} to="/portfolio" size="small">
                                Portfolio
                            </Button>
                            }
                        </Box>
                        <Box>
                            {notSmallScreen &&
                            <Button variant="contained" color="primary" component={RouterLink} to="/blog">
                                Blog
                            </Button>
                            }
                            {smallScreen &&
                            <Button variant="contained" color="primary" component={RouterLink} to="/blog" size="small">
                                Blog
                            </Button>
                            }
                        </Box>
                        <Box p={1}>
                            {notSmallScreen &&
                            <Button variant="contained" color="primary" component={RouterLink} to="/tutorial">
                                Tutorials
                            </Button>
                            }
                            {smallScreen &&
                            <Button variant="contained" color="primary" component={RouterLink} to="/tutorial" size="small">
                                Tutorials
                            </Button>
                            }
                        </Box>
                        <Box>
                            {notSmallScreen &&
                            <Switch checked={props.darkMode} onChange={() => props.setDarkMode(!props.darkMode)}/>
                            }
                            {smallScreen &&
                            <Switch checked={props.darkMode} onChange={() => props.setDarkMode(!props.darkMode)} size="small"/>
                            }
                        </Box>
                    </Toolbar>
            </AppBar>
            </div>
        );
    }

export default withRouter(Navbar);
