import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import Typography from '@material-ui/core/Typography';
import './Header.css'

class Header extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        if (this.props.subtitle) {
            return (
                <React.Fragment>
                    <header>
                        <div id="header">
                            <Typography variant="h1">
                                {this.props.header}
                            </Typography>
                            <Typography variant="subtitle1">
                                {this.props.subtitle}
                            </Typography>
                        </div>
                    </header>
                </React.Fragment>
            );
        } else {
            return (
                <React.Fragment>
                    <header>
                        <div id="header">
                            <Typography variant="h1">
                                {this.props.header}
                            </Typography>
                        </div>
                    </header>
                </React.Fragment>
            );
        }
    }
}

export default withRouter(Header);
