import React from 'react';
import { useState } from 'react';
import withRouter from 'react-router-dom/withRouter';
import axios from 'axios';
import Header from './Header';

let result = {};

axios.get('http://127.0.0.1:5000/api/articles')
    .then(res => {
        result = res.data
    })

function BlogContent() {
    const [articles, setArticles] = useState()

    setArticles(result)
    console.log(articles)

    return (
        <React.Fragment>
            <Header header={'Blog'} subtitle={'Articles about various topics related to the tech industry'}/>
            {articles ? articles.map(article => <p>{article.title}, {article.publish_date.$date}</p>): <p>loading...</p>}
        </React.Fragment>
    );
}


export default withRouter(BlogContent);
